mapper.select:
SELECT a.ifmmSeq , a.ifmmDelNy , a.ifmmName , a.ifmmId , a.ifmmDob , a.regDateTimeSvr , b.ifmeNumber ,(select ifcdName from infrCode where ifcdSeq = a.ifmmGenderCd) as ifmmGender FROM nct.infrMember a left join infrMemberPhone b on b.ifmmSeq = a.ifmmSeq WHERE 1=1
AND ifmmName LIKE concat('%', #{shValue} ,'%') AND ifmmId LIKE concat('%', #{shValue} ,'%')
limit #{startRnumForMysql}, #{rowNumToShow}

mapper.insert:
insert into nct.infrMember(
	  ifmmName
	  ,ifmmEngName
	  ,ifmmId
	  ,ifmmGenderCd
	  ,ifmmPassword
      ,ifmmDesc
      ,ifmmDob	      
	, ifmmDelNy	
	, regDateTimeSvr
	, ifmmSavedCd
	,originalFileName
	,uuidFileName
)values(
	#{ifmmName}
	,#{ifmmEngName}
	,#{ifmmId}
	,#{ifmmGenderCd}
	,#{ifmmPassword}
	,#{ifmmDesc}
	,#{ifmmDob}
	,0
	,NOW()
	,#{ifmmSavedCd}
	,#{originalFileName}
	,#{uuidFileName}
)

	SELECT last_insert_id()

mapper.update:
UPDATE nct.infrMember SET 
	ifmmPassword  = #{ifmmPassword}
	,ifmmDesc  = #{ifmmDesc}
WHERE 1=1
	AND ifmmSeq= #{ifmmSeq}

UPDATE nct.infrMemberAddress SET 
	ifmaAddress1  =  #{ifmaAddress1}
	,ifmaAddress2  = #{ifmaAddress2}
WHERE 1=1
	AND ifmmSeq= #{ifmmSeq};

UPDATE nct.infrMemberEmail SET 
	ifmeEmailFull  =  #{ifmeEmailFull}
WHERE 1=1
	AND ifmmSeq= #{ifmmSeq};

UPDATE nct.infrMemberPhone SET 
	ifmeNumber  =  #{ifmeNumber}
	,ifmeTelecomCd  =  #{ifmeTelecomCd}
WHERE 1=1
	AND ifmmSeq= #{ifmmSeq};
